    private List<Usuario> usuarios = new();
    private bool loading = true;

    protected override async Task OnInitializedAsync()
    {
        await LoadUsuarios();
    }

    private async Task LoadUsuarios()
    {
        try
        {
            usuarios = await Http.GetFromJsonAsync<List<Usuario>>("api/usuarios") ?? new();
        }
        catch (Exception ex)
        {
            Snackbar.Add("Error al cargar usuarios: " + ex.Message, Severity.Error);
        }
        finally
        {
            loading = false;
        }
    }

    private async Task OpenDialog(Usuario usuario)
    {
        var parameters = new DialogParameters
        {
            ["usuario"] = usuario
        };

        var dialog = await DialogService.ShowAsync<UsuarioDialog>("Usuario", parameters);
        var result = await dialog.Result;

        if (!result.Canceled)
        {
            await LoadUsuarios();
        }
    }

    private async Task DeleteUsuario(Usuario usuario)
    {
        var confirm = await DialogService.ShowMessageBox(
            "Confirmar",
            "¿Está seguro de eliminar este usuario?",
            yesText: "Eliminar",
            noText: "Cancelar"
        );

        if (confirm ?? false)
        {
            try
            {
                await Http.DeleteAsync($"api/usuarios/{usuario.Id}");
                await LoadUsuarios();
                Snackbar.Add("Usuario eliminado correctamente", Severity.Success);
            }
            catch (Exception ex)
            {
                Snackbar.Add("Error al eliminar usuario: " + ex.Message, Severity.Error);
            }
        }
    }

